import Button from "@mui/material/Button";
import CircularProgress, {
  circularProgressClasses,
} from "@mui/material/CircularProgress";
import Box from "@mui/material/Box";
import { useState } from "react";
function CircularIndeterminate() {
  return (
    <Box sx={{ display: "flex" }}>
      <CircularProgress />
    </Box>
  );
}

function TriggerProgressBar() {
  const [downloading, setDownloading] = useState(false);

  const buttonToggle = document.getElementsByClassName("button-toggle");
  function handleClick() {
    setDownloading(true);

    setTimeout(() => {
      console.log(`Timeout worked`);
      setDownloading(false);
    }, 3000);

    // if (index === false) {
    //   setIndex(true);
    //   console.log("Setted to true");
    // } else {
    //   setIndex(false);
    //   setTimeout(() => {
    //     console.log(`Timeout worked`);
    //     setIndex(true);
    //   }, 3000);
    //   console.log("Setted to false");
    // }
  }
  return (
    <div className="parcing-animation">
      {/* <Button
        className={`button-toggle` + (index === false ? " display-none" : "")}
        onClick={handleClick}
      >
        Start Parcing
      </Button> */}
      <Button className="button-toggle" onClick={handleClick} type="submit">
        Start parcing
      </Button>
      <div className="parcing-animation_cirle">
        {downloading && <CircularIndeterminate />}
      </div>
    </div>
  );
}

function CircularProgressWithLabel(props) {
  return (
    <Box sx={{ position: "relative", display: "inline-flex" }}>
      <CircularProgress variant="determinate" {...props} />
      <Box
        sx={{
          top: 0,
          left: 0,
          bottom: 0,
          right: 0,
          position: "absolute",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
        }}
      >
        <Typography variant="caption" component="div" color="text.secondary">
          {`${Math.round(props.value)}%`}
        </Typography>
      </Box>
    </Box>
  );
}

CircularProgressWithLabel.propTypes = {
  /**
   * The value of the progress indicator for the determinate variant.
   * Value between 0 and 100.
   * @default 0
   */
  value: PropTypes.number.isRequired,
};

function CircularWithValueLabel() {
  const [progress, setProgress] = React.useState(10);

  React.useEffect(() => {
    const timer = setInterval(() => {
      setProgress((prevProgress) =>
        prevProgress >= 100 ? 0 : prevProgress + 10
      );
    }, 800);
    return () => {
      clearInterval(timer);
    };
  }, []);

  return <CircularProgressWithLabel value={progress} />;
}
export default TriggerProgressBar;
